import axios from "axios";
import React, { useContext, useState } from 'react';
import { SessionContext } from "../../contexts/SessionContext";
import { BudgetDisplayContext } from "../../contexts/BudgetDisplayContext";


function AddAccountData() {
  const { username } = useContext(SessionContext);
  const { hasBudget, setHasBudget, database } = useContext(BudgetDisplayContext);
  const token = window.localStorage.getItem("token");

  const [accountData, setAccountData] = useState({
    monthly_income: 0,
    monthly_expense: 0,
    username: username,
    database: database
  });

  // Handle input data changes
  const handleChange = (e) => {
    const value = e.target.value;
    setAccountData({ 
      ...accountData,
      [e.target.name]: value
    });
  }

  // Handle bad input
  const badAdd = () => {
    alert(`WARNING! Missing ${username}'s new monthly account data:\n\n${!accountData.monthly_income ? 'Monthly Income' : ''}\n${!accountData.monthly_expense ? 'Monthly Expense' : ''}`);
  }

  // Add budget data to user's account
  const add = () => {
    axios.post('http://localhost:3010/add-account', accountData, {
      headers: {
        'Authorization': `Bearer ${token}`,
      }
    })
    .then((res) => {
      (res.data) && console.log(res.data);
      (res.data && !hasBudget) && setHasBudget(true);
      (res.data) && window.location.reload();
    })
    .catch((err) => {
      console.log(err);
    });
  }

  return (
    // <>
    //   AddAccountData
    // </>
    <div className="App">
      <div id='add-account-data'/>
      <h1><strong>Add your monthly account data!</strong></h1>
      <p>Hint: <strong>You can also use these fields to update your account information!</strong></p>
      <br/>
      <>
        <label>
          <h2>Monthly Income</h2>
          <input alt='monthly income' className='add-data' autoComplete='off' onChange={handleChange} name="monthly_income" type='number' placeholder='Enter your budget category here...'/>
        </label>
        <label>
          <h2>Monthly Expense</h2>
          <input alt='monthly expense' className='add-data' autoComplete='off' onChange={handleChange} name="monthly_expense" type='number' placeholder='Enter your category amount here...'/>
        </label>
      </>
      <br/><br/><br/><br/>
      <button className='add-data-button button' type='submit' onClick={(accountData.monthly_income || accountData.monthly_expense) ? add : badAdd}>Add</button>
    </div>
  )
}


export default AddAccountData;
