import { useState, createContext, useRef } from 'react';

// const LoginContext = createContext();

function LoginHook() {
  console.log('LoginHook!');
  let [loggedIn, setLoggedIn] = useState(false);


  // Login
  const login = () => {
    console.log('Login!');

    axios.post('http://localhost:3010/login', data)
    .then((data) => {
      console.log(data);
      // window.location.href = '/'
      (data.data && data.data !== 'BAD_USERNAME') ? navigate('/') :
        (data.data === 'BAD_USERNAME') ? alert('This user does not exist! Pleas try again with a different username') :
        alert('Incorrect password! Please try again');
    })
    .catch((err) => {
      console.log(err);
    });
  };


  return (
    // <LoginContext.Provider value={{loggedIn, setLoggedIn}}/>
    // <LoginContext/>
    // ,
    {loggedIn}
    // loggedIn
    // setLoggedIn
  );
}


