import axios from 'axios';
// import bcryptjs from 'bcryptjs';
import React, { useState, useRef, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { SessionContext } from '../../contexts/SessionContext';


function SignupPage(props) {
  const [userInfo, setUserInfo] = useState({
    username: "",
    password: ""
  });

  const badSignup = (e) => {
    alert(`WARNING!\nMissing signup information:\n\n${!userInfo.username ? 'Username' : ''}\n${!userInfo.password ? 'Password' : ''}`)
  }

  const handleChange = (e) => {
    const value = e.target.value;
    setUserInfo({
      ...userInfo,
      [e.target.name]: value
    });
  };

  const login = () => {
    console.log('Login!');
  }

  const signup = () => {
    console.log('Signup!');
    console.log(userInfo.username, userInfo.password);

    // ...
    // ---
  }

  const handleEnterPress = (e) => {
    if(e.key === 'Enter') {
      (userInfo.username && userInfo.password) ? signup() : badSignup();
    }
  }

  // Accessibility scroll
  const signupRef = useRef(null);
  setTimeout(
    () => {
      if (signupRef.current) {
        signupRef.current.scrollIntoView({
          behavior: 'smooth'
        })
      }
    },
    250
  );


  return(
    // <div>
    //   Signup!
    // </div>
    <div className='App'>
      {/* In-page navigation */}
      <div id='signup' ref={signupRef}/>
      <h1>SIGNUP</h1>
      <h2>Please sign up for an account below to begin budgeting...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        <input alt='login username' className='icon un' onChange={handleChange} autoComplete='off' name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        <input alt='login password' className='icon pw'  onKeyUp={handleEnterPress} onChange={handleChange} autoComplete='off' name='password' type='password' placeholder='Enter your password here...' required/>
      </label>
      <br/><br/><br/>
      <button className='signup-button button' type='submit' onClick={(userInfo.username && userInfo.password) ? signup : badSignup}>Create account</button>
      <br/><br/><br/>
      <h3>Already have an account with us? <a href='/login#login' onClickCapture={login}>Login here!</a></h3>
    </div>
  )
}

export default SignupPage;
