  // useEffect(() => {
  //   data.setLoggedIn(JSON.parse(window.sessionStorage.getItem("loggedIn")));
  // }, [])
  // useEffect(() => {
  //   window.sessionStorage.setItem("loggedIn", data.loggedIn);
  // }, [])

  return (
    <LoginContext.Provider value={{loggedIn, setLoggedIn}}>
      <App/>
    </LoginContext.Provider>
  )
}

export default LoginHook;
