import axios from 'axios';
import React, { useState, useRef, useEffect } from 'react';
import bcrypt from 'bcryptjs';
import show from '../../images/show.png';
import HomePage from '../HomePage/HomePage';
const saltRounds = 8;

// Encrypt password
// function encryptPassword(pw) {return bcrypt.hash(pw, bcrypt.genSalt(saltRounds));}
function encryptPasswordSync(pw) {return bcrypt.hashSync(pw, bcrypt.genSaltSync(saltRounds));}


function loginUser() {
  axios.post('http://localhost:3010/login', (req, res) => {
    res.send({
      username: '',
      password: ''
    })
  })
  .then((data) => {
    console.log(data);
  })
  .catch((err) => {
    console.log(err);
  })
}


function LoginPage(props) {
  let LOGGED_IN = props.loggedIn;
  // console.log(props.loggedIn);

  const [data, setData] = useState({
    username: "",
    password: ""
  });

  const emptyLogin = (e) => {
    alert(`WARNING!\nMissing login information:\n\n${!data.username ? 'Username' : ''}\n${!data.password ? 'Password' : ''}`)
  }

  const handleChange = (e) => {
    // const value = (e.target.name === 'username') ? e.target.value : encryptPasswordSync(e.target.value);
    const value = e.target.value;
    // console.log(e.target.name, value)

    setData({
      ...data,
      [e.target.name]: value
    });
    // console.log(data);
  };

  const signup = () => {
    console.log('Signup!');
  }

  const login = () => {
    console.log('Login!');
    // console.log(data.username, data.password);

    // props.loggedIn = true;

    axios.post('http://localhost:3010/login', data)
    .then((data) => {
      console.log(data);
      window.location.href = data.data ? '/' : null;
    })
    .catch((err) => {
      console.log(err);
    })
  };

  const handleEnterPress = (e) => {
    if(e.key === 'Enter') {
      (data.username && data.password) ? login() : emptyLogin();
    }
  }

  // Accessibility scroll
  const loginRef = useRef(null);
  // setTimeout(
  //   () => {
  //     if (loginRef.current) {
  //       loginRef.current.scrollIntoView({
  //         behavior: 'smooth'
  //       })
  //     }
  //   },
  //   250
  // );

  return (
    // <div>
    //   LoginPage
    // </div>
    !props.loggedIn ?
    <div className='App'>
      {/* In-page navigation */}
      <div id='login' ref={loginRef}/>
      <h1>LOGIN</h1>
      <h2>Please login below to see your budget details...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        <input alt='login username' className='icon un' onChange={handleChange} autoComplete='off' name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        <input alt='login password' className='icon pw' onKeyUp={handleEnterPress} onChange={handleChange} autoComplete='off' name='password' type='password' placeholder='Enter your password here...' required/>
        {/* <img src={require('../../images/show.png')} style={{'height': '24px', 'marginLeft': '-30px'}} alt='display password'/> */}
      </label>
      <br/><br/><br/>
      <button className='login-button button' type='submit' onClick={(data.username && data.password) ? login : emptyLogin}>Login</button>
      <br/><br/><br/>
      <h3>Don't have an account with us? <a href='/signup#signup' onClickCapture={signup}>Sign up here!</a></h3>
    </div>
    : null
  );
}

export default LoginPage;
