import axios from "axios";
import React, { useContext, useState } from 'react';
import { SessionContext } from "../../contexts/SessionContext";
import AddAccountData from "../UpdateData/AddAccountData";
import user_icon from "../../images/user.png"
import DeleteAccountPopup from "../Popups/DeleteAccountPopup";


function SettingsPage() {
  const { username } = useContext(SessionContext);

  const showPopup = () => {
    document.getElementById('popup-overlay').style.display = 'block';
    document.getElementById('delete-account-popup').style.display = 'block';
  }

  return (
    <>
      <div id='settings-page-section'/>
      <div className="container center">
        <h1>{username}'s Account Settings</h1>
        <img src={user_icon} alt="user icon placeholder" style={{"height": "96px", "width": "96px"}}/>
        <AddAccountData/>
        <h1 style={{"color": "red"}}>TODO: DELETE ACCOUNT FUNCTIONALITY</h1>
        <button className="delete-account-button button" onClick={showPopup}>DELETE</button>
        <DeleteAccountPopup/>
        <div style={{"marginTop": "2048px"}}/>
      </div>
    </>
  )
}


export default SettingsPage;
