import axios from 'axios';
import * as d3 from 'd3';
import React, { useContext } from 'react';
import { SessionContext } from '../contexts/SessionContext';
import { BudgetDisplayContext } from '../contexts/BudgetDisplayContext';

function D3Chart() {
  const { username } = useContext(SessionContext);
  const { database } = useContext(BudgetDisplayContext);

  console.log(username, database);

  function getData() {
    axios.post('http://localhost:3010/display',
      {
        database: database,
        username: username
      }
    )
    .then(function (res) {
        console.log(res.data[1]);
        console.log(res.data[2]);
        console.log(res.data[3]);

        // generateChart();

      /**/
        var svg = d3.select("figure#d3-donut-container")
        .append("svg")
        .append("g")

        svg.append("g")
            .attr("class", "slices");
        svg.append("g")
            .attr("class", "labels");
        svg.append("g")
            .attr("class", "lines");

        var width = 960,
            height = 450,
            radius = Math.min(width, height) / 2;

        var pie = d3.pie()
            .sort(null)
            .value(function(d) {
                return d.value;
            });

        var arc = d3.arc()
            .outerRadius(radius * 0.8)
            .innerRadius(radius * 0.4);

        var outerArc = d3.arc()
            .innerRadius(radius * 0.9)
            .outerRadius(radius * 0.9);

        svg.attr("transform", "translate(" + width / 2 + "," + height / 2 + ")");

        var key = function(d){ return d.data.label; };

        var color = d3.scaleOrdinal()
        .domain(res.data[1])
        .range(res.data[2]);

        // console.log(color);

        function startData() {
            var labels = color.domain();
            return labels.map((label, index) => {
                return { label: label, value: res.data[3][index] } // --- JSON.parse(sessionStorage.getItem('amount'))[index]
            });
        }
        change(startData());

        function randomData() {
            var labels = color.domain();
            return labels.map(function(label){
                return { label: label, value: Math.random() }
            });
        }
        // --- change(randomData());

        d3.select(".randomize")
            .on("click", function(){
                // -- getColor();
                change(randomData());
            });

        d3.select(".reset")
            .on("click", function(){
                // -- getColor();
                change(startData());
            });

        function change(data) {

            // ------- PIE SLICES -------
            var slice = svg.select(".slices").selectAll("path.slice")
                .data(pie(data), key);

            slice.enter()
                .insert("path")
                .style("fill", function(d) { return color(d.data.label); })
                .attr("class", "slice");

            slice		
                .transition().duration(1000)
                .attrTween("d", function(d) {
                    this._current = this._current || d;
                    var interpolate = d3.interpolate(this._current, d);
                    this._current = interpolate(0);
                    return function(t) {
                        return arc(interpolate(t));
                    };
                })

            slice.exit()
                .remove();

            // ------- TEXT LABELS -------

            var text = svg.select(".labels").selectAll("text")
                .data(pie(data), key);

            text.enter()
                .append("text")
                .attr("dy", ".35em")
                .text(function(d) {
                    return d.data.label;
                });

            function midAngle(d){
                return d.startAngle + (d.endAngle - d.startAngle)/2;
            }

            text.transition().duration(1000)
                .attrTween("transform", function(d) {
                    this._current = this._current || d;
                    var interpolate = d3.interpolate(this._current, d);
                    this._current = interpolate(0);
                    return function(t) {
                        var d2 = interpolate(t);
                        var pos = outerArc.centroid(d2);
                        pos[0] = radius * (midAngle(d2) < Math.PI ? 1 : -1);
                        return "translate("+ pos +")";
                    };
                })
                .styleTween("text-anchor", function(d){
                    this._current = this._current || d;
                    var interpolate = d3.interpolate(this._current, d);
                    this._current = interpolate(0);
                    return function(t) {
                        var d2 = interpolate(t);
                        return midAngle(d2) < Math.PI ? "start":"end";
                    };
                });

            text.exit()
                .remove();

            // ------- SLICE TO TEXT POLYLINES -------

            var polyline = svg.select(".lines").selectAll("polyline")
                .data(pie(data), key);

            polyline.enter()
                .append("polyline");

            polyline.transition().duration(1000)
                .attrTween("points", function(d){
                    this._current = this._current || d;
                    var interpolate = d3.interpolate(this._current, d);
                    this._current = interpolate(0);
                    return function(t) {
                        var d2 = interpolate(t);
                        var pos = outerArc.centroid(d2);
                        pos[0] = radius * 0.95 * (midAngle(d2) < Math.PI ? 1 : -1);
                        return [arc.centroid(d2), outerArc.centroid(d2), pos];
                    };			
                });

            polyline.exit()
                .remove();
        };
      /**/
    })
    .catch((err) => {
      console.log(err);
    });

    function generateChart() {
      console.log(d3.pie());
      console.log(d3.arc());
      console.log(d3.scaleOrdinal());
    }
  };

  getData();

  return (
    <figure id="d3-donut-container"/>
  );
}


export default D3Chart;
