      <h1>LOGIN</h1>
      <h2>Please login below to see your budget details...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        {/* <img src='' alt='login icon'/><span style={{'padding-right':  10}}/> */}
        <input className='icon un' onChange={handleChange} id='username' name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        {/* <img src='' alt='password icon'/><span style={{'padding-right':  10}}/> */}
        <input className='icon pw' onChange={handleChange} id='password' name='password' type='password' placeholder='Enter your password here...' required/>
      </label>
      <br/><br/><br/>
      <button className='login-button' type='submit' onClick={(true) ? login : null}>Login</button>
      <br/><br/>
    </div>
  );
}

export default LoginPage;
