import React from 'react';
import { useContext, useEffect, useRef, useState } from 'react';
import { SessionContext } from '../../contexts/SessionContext';
import { BudgetDisplayContext } from '../../contexts/BudgetDisplayContext';
import AddBudgetData from '../AddBudgetData/AddBudgetData';
import DonutChart from '../../charts/DonutChart';
import D3Chart from '../../charts/D3Chart';


function HomePage() {
	// console.log('Landed on the homepage!');
	let [time, setTime] = useState(0);

	const timeRef = useRef(null);

	const { loggedIn, setLoggedIn, username, setUsername } = useContext(SessionContext);
	const { hasBudget } = useContext(BudgetDisplayContext);const token = window.localStorage.getItem("token");

	const timeLeft = (e) => {
		const newTime = time - 1;
		return { newTime };
	}

	const startTimer = (e) => {
		let { newTime } = timeLeft(e);

		if (newTime >= 0) {
			setTime(newTime);
		}
	}

	const clearTimer = (e) => {
		setTime(20);

		if (timeRef.current) clearInterval(timeRef.current);

		const id = setInterval(() => {
			startTimer(e);
		}, 1000);
		timeRef.current = id;
	}

	useEffect(() => {
		clearTimer(20);
	}, [])
	
	// useEffect(() => {
	// 	var countdownTimer = setInterval(
	// 		() => {
	// 			// time = time - 1;
	// 			incrementTime(time - 1);
	// 			console.log(time);
				
	// 			if (time <= 0){
	// 				document.getElementById('token-popup').style.display = 'none';
	// 				clearInterval(countdownTimer);
	// 			}
	// 		},
	// 		1000
	// 	);
	// }, [])
	
  // Token verification
  useEffect(() => {
    console.log(loggedIn, token);
    (loggedIn && token) &&
    setTimeout(
      () => {
        document.getElementById('token-popup').style.display = 'block';
				// countdownTimer;
      },
      2000
    );
  }, []);

	// Hide Popup
	const hidePopup = () => {
		document.getElementById('token-popup').style.display = 'none';
	}

	// Logout
  const logout = () => {
    console.log('Logout!');
    window.localStorage.setItem('loggedIn', false);
    window.localStorage.setItem('username', '');
    window.localStorage.removeItem("token");
    setLoggedIn(false);
    setUsername('');
		hidePopup();
  }

	return (
		<>
			<div id='token-popup' className='center token-popup' style={{"display": "none"}}>
				<h1 style={{"color": "black"}}>WARNING!</h1>
				<h2 style={{"color": "black"}}>Your session is about to expire (in <strong>{time}</strong> seconds). Would you like to refresh your session?</h2>
				<button onClick={hidePopup} className='token-button button'>Yes</button>
				<button onClick={logout} className='token-button button' style={{"marginLeft": "40px"}}>No</button>
			</div>
			<div className="container center">

				{/* <div id='view-budget-data'/> */}
				
				<main id="main">
								
					{/* <!-- This is a Semantic HTML Change --> */}
					<aside>
						{/* <!-- This is a Semantic HTML Change --> */}

						<article className="text-box">
							{
							!loggedIn && username === '' ?
								<>
									<h1>Take it for a spin!</h1>
									<p>
										Try it out! Take a look at some of the features below. Poke around a bit
										to get a feel for the app for yourself!
									</p>
									<DonutChart/>
									<D3Chart/>
								</>
							: (loggedIn && hasBudget) ?
								<>
									<h1>Welcome {username}!</h1>
									<p>
										We've gathered all of your budget information and displayed it for you below. 
									</p>
									<DonutChart/>
									<D3Chart/>
									<AddBudgetData/>
								</>
							:
								<>
									<h1>Welcome {username}!</h1>
									<p>
										<br/><br/>
										Don't have any budget information displayed here?
										<br/><br/>
										<strong>Take a minute to enter in some data!</strong>
									</p>
									<br/>
									<DonutChart/>
									<D3Chart/>
									{/* <h2>Enter some data to get started...</h2> */}
									<AddBudgetData/>
								</>
							}
						</article>
					</aside>
					{/* <PieChart/>
					<D3Chart/> */}
					{/* <D3Chart data={final_data} innerRadius={0} outerRadius={350}/> */}
				</main>

			</div>
		</>
  );
}

export default HomePage;
