import axios from "axios";
import React, { useRef, useContext, useState } from 'react';
import { SessionContext } from "../../contexts/SessionContext";
import { BudgetDisplayContext } from "../../contexts/BudgetDisplayContext";


function AddBudgetData() {
  const { username } = useContext(SessionContext);
  const { hasBudget, database } = useContext(BudgetDisplayContext);

  const [budgetData, setBudgetData] = useState({
    category: "",
    amount: "",
    color: ""
  });

  // ...

  // Handle hexadecimal color key presses
  const handleChange = (e) => {
    // console.log(e.key);
    const value = e.target.value;
    setBudgetData({ 
      ...budgetData,
      [e.target.name]: value
    });
  }

  // Accessibility scroll
  const addDataRef = useRef(null);
  // setTimeout(
  //   () => {
  //     if (addDataRef.current) {
  //       addDataRef.current.scrollIntoView({
  //         behavior: 'smooth'
  //       })
  //     }
  //   },
  //   250
  // );

  return (
    // <>
    //   AddBudgetData
    // </>
    <div className="App">
      <div id="addData" ref={addDataRef}/>
      <p><strong>Add some data!</strong></p>
      <br/>
      <>
        <label>
          <h2>Category</h2>
          {/* Banana */}
          <input alt='budget category' className='add-data' autoComplete='off' onChange={handleChange} type='text' placeholder='Enter your budget category here...' required/>
        </label>
        <label>
          <h2>Amount</h2>
          {/* 370 */}
          <input alt='category amount' className='add-data' autoComplete='off' onChange={handleChange} type='number' placeholder='Enter your category amount here...' required/>
        </label>
        <label>
          <h2>Color (Hex)</h2>
          {/* #F8E16C */}
          <input alt='category color' className='add-data' autoComplete='off' onChange={handleChange} type='color' placeholder='Enter your preferred color here...' required/>
        </label>
      </>
      <br/><br/><br/><br/>
      <button className='add-data-button button' type='submit' onClick={() => {}}>Add</button>
    </div>
  )
}


export default AddBudgetData;
