import React, { useContext,  } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { SessionContext } from '../../contexts/SessionContext';

import '../../index.scss';
import yt_logo from '../../images/yt-logo.gif';

function Menu() {
  const { loggedIn, setLoggedIn, setUsername } = useContext(SessionContext);

  const logout = () => {
    console.log('Logout!');
    window.sessionStorage.setItem('loggedIn', false);
    window.sessionStorage.setItem('username', '');
    setLoggedIn(false);
    setUsername('');
  }

  const navigate = useNavigate();
  
  return (
    // <div>
    //   Menu
    // </div>
    <div className="menu">
        {/* <h1>Logged in? {loggedIn}</h1> */}
        <ul>
            <li><Link to="/">Homepage</Link></li>
            <li><Link to="/about">About</Link></li>

            {!loggedIn ?
            <li><Link to="/login">Login</Link></li>
            : <li><Link onClick={logout}>Logout</Link></li>}

            {!loggedIn ?
            <li><Link to="/signup">Signup</Link></li>
            : null}
            
            <li><Link to="https://google.com">Google</Link></li>
            {/* <!-- This is an SEO Change --> */}
            <li><Link to="https://www.youtube.com/watch?v=EsOTfVIcdEI" rel="nofollow noopener noreferrer">YouTube</Link></li>
            {/* <!-- This is an SEO Change --> */}
            {/* <li><img src="images/yt-logo.gif" alt="YouTube logo" style="width:40px; height:40px"></img></li> */}
            <img src={yt_logo} onClick={() => {window.location.href = 'https://www.youtube.com/watch?v=EsOTfVIcdEI'}} alt="YouTube logo" style={{width: "40px", height: "40px"}}/>
        </ul>
    </div>
  );
}

export default Menu;
