import { useState } from 'react';
import { SessionContext } from '../contexts/SessionContext';
import App from '../App';


function LoginHook() {
  console.log('LoginHook is a parent to App!');

  console.log('getUsername: ', `window.sessionStorage.getItem('username')`);

  const [loggedIn, setLoggedIn] = useState(
    window.sessionStorage.getItem('loggedIn') === 'true' ?
    true : false
  );
  const [username, setUsername] = useState(
    (window.sessionStorage.getItem('username') !== '') ?
    null : null 
  );

  if (!window.sessionStorage.getItem('loggedIn')) window.sessionStorage.setItem('loggedIn', false);
  if (!window.sessionStorage.getItem('username')) window.sessionStorage.setItem('username', '');

  return (
    <SessionContext.Provider value={{loggedIn, setLoggedIn, username, setUsername}}>
      <App/>
    </SessionContext.Provider>
  )
}

export default LoginHook;
