import axios from 'axios';
import React, { useState } from 'react';


function loginUser() {
  axios.post('/login', (req, res) => {
    console.log("req.data: ", req.data);
    console.log("res.data: ", res.data);
  })
  .then((data) => {
    // ...
    // ---

    console.log(data);
  })
  .catch((err) => {
    // ...
    // ---

    console.log(err);
  })
}


function LoginPage() {
  let username = document.getElementById('username');
  let password = document.getElementById('password');
  // const [data, setData] = useState({
  //   username: "",
  //   password: ""
  // });

  // const handleChange = (e) => {
  //   const value = e.target.value;
  //   setData({
  //     ...data,
  //     [e.target.name]: value
  //   });
  // };

  // debugger;
  const login = (element) => {
    console.log(element.target);
    console.log(username, password);
    // element.preventDefault();

    axios
      .post('/login', (req, res) => {
        // ...
        // ---

        console.log("req.data: ", req.data);
        console.log("res.data: ", res.data);
      })
      .then((data) => {
        // ...
        // ---

        console.log(data);
      })
      .catch((err) => {
        // ...
        // ---

        console.log(err);
      })
  };

  return (
    // <div>
    //   LoginPage
    // </div>
    <div className='App'>
      <h1>LOGIN</h1>
      <h2>Please login below to see your budget details...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        {/* <img src='' alt='login icon'/><span style={{'padding-right':  10}}/> */}
        <input value={'A'} className='icon un' id='username' name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        {/* <img src='' alt='password icon'/><span style={{'padding-right':  10}}/> */}
        <input value={'B'} className='icon pw' id='password' name='password' type='password' placeholder='Enter your password here...' required/>
      </label>
      <br/><br/><br/>
      <button className='login-button' type='submit' onClick={loginUser}>Login</button>
      <br/><br/>
    </div>
  );
}

export default LoginPage;
