import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { LoginContext } from '../../contexts/LoginContext';
import LoginHook  from '../../hooks/LoginHook';

import '../../index.scss';
import yt_logo from '../../images/yt-logo.gif';

function Menu() {
  const { setLoggedIn } = useContext(LoginContext);
  
  return (
    // <div>
    //   Menu
    // </div>
    // <LoginHook>
    <div className="menu">
        <ul>
            <li><Link to="/">Homepage</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/login">Login</Link></li>
            <li><Link to="/signup">Signup</Link></li>
            <li><Link to="https://google.com">Google</Link></li>
            {/* <!-- This is an SEO Change --> */}
            <li><Link to="https://youtube.com" rel="nofollow noopener noreferrer">YouTube</Link></li>
            {/* <!-- This is an SEO Change --> */}
            {/* <li><img src="images/yt-logo.gif" alt="YouTube logo" style="width:40px; height:40px"></img></li> */}
            <img src={yt_logo} alt="YouTube logo" style={{width: "40px", height: "40px"}}/>
        </ul>
        <button style={{"line-height": "30px"}} onClick={() => {setLoggedIn(true)}}/>
    </div>
    // </LoginHook>
  );
}

export default Menu;
