import { useState } from 'react';
import { LoginContext } from '../contexts/LoginContext';
import App from '../App';


function LoginHook() {
  console.log('LoginHook is a parent to App!');

  const [loggedIn, setLoggedIn] = useState(
    window.sessionStorage.getItem('loggedIn') === 'true' ?
    true : false
  );
  const [username, setUsername] = useState(
    window.sessionStorage.getItem('username') !== '' ?
    '' : null
  );

  if (!window.sessionStorage.getItem('loggedIn')) window.sessionStorage.setItem(false);
  if (!window.sessionStorage.getItem('username')) window.sessionStorage.setItem('');

  return (
    <LoginContext.Provider value={{loggedIn, setLoggedIn, username, setUsername}}>
      <App/>
    </LoginContext.Provider>
  )
}

export default LoginHook;
