import { useState } from 'react';
import { SessionContext } from '../contexts/SessionContext';
import App from '../App';


function LoginHook() {
  console.log('SessionHook is a parent to App!')

  const [loggedIn, setLoggedIn] = useState(
    window.sessionStorage.getItem('loggedIn') === 'true' ?
    true : false
  );
  const [username, setUsername] = useState(
    window.sessionStorage.getItem('username') !== null ?
    window.sessionStorage.getItem('username') : ''
  );
  const [database, setDatabase] = useState(
		(loggedIn && username !== '') ?
		'personal-budget' : 'display'
	);

  // console.log('getUsername: ', `'${window.sessionStorage.getItem('username')}'`);
  // console.log('username', `'${username}'`)

  if (!window.sessionStorage.getItem('loggedIn')) window.sessionStorage.setItem('loggedIn', false);
  if (window.sessionStorage.getItem('username') === null) window.sessionStorage.setItem('username', '');

  return (
    <SessionContext.Provider value={{loggedIn, setLoggedIn, username, setUsername, database, setDatabase}}>
      <App/>
    </SessionContext.Provider>
  )
}

export default LoginHook;
