import axios from 'axios';
import React, { useState, useRef, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { LoginContext } from '../../contexts/LoginContext';
import LoginHook from '../../hooks/LoginHook';


function LoginPage() {
  // User info state
  const [data, setData] = useState({
    username: "",
    password: ""
  });

  // Bad login attempt
  const badLogin = (e) => {
    alert(`WARNING! Missing login information:\n\n${!data.username ? 'Username' : ''}\n${!data.password ? 'Password' : ''}`)
  }

  // Handle input data gathering
  const handleChange = (e) => {
    const value = e.target.value;

    setData({ 
      ...data,
      [e.target.name]: value
    });
  };

  // Navigate
  const navigate = useNavigate();

  // Login context
  const {setLoggedIn} = useContext(LoginContext);

  // Signup
  const signup = () => {
    console.log('Signup!');
  }

  // Login
  const login = () => {
    console.log('Login!');

    axios.post('http://localhost:3010/login', data)
    .then((data) => {
      console.log(data);
      // window.location.href = '/'
      (data.data && data.data !== 'BAD_USERNAME') ? navigate('/') :
        (data.data === 'BAD_USERNAME') ? alert('This user does not exist! Pleas try again with a different username') :
        alert('Incorrect password! Please try again');

      if (data.data === true) setLoggedIn(data.data);
    })
    .catch((err) => {
      console.log(err);
    });
  };

  // Allow 'Enter' key press
  const handleEnterPress = (e) => {
    if(e.key === 'Enter') {
      (data.username && data.password) ? login() : badLogin();
    }
  }

  // Accessibility scroll
  const loginRef = useRef(null);
  // setTimeout(
  //   () => {
  //     if (loginRef.current) {
  //       loginRef.current.scrollIntoView({
  //         behavior: 'smooth'
  //       })
  //     }
  //   },
  //   250
  // );

  return (
    // <div>
    //   LoginPage
    // </div>
    <div className='App'>
      {/* In-page navigation */}
      <div id='login' ref={loginRef}/>
      <h1>LOGIN</h1>
      <h2>Please login below to see your budget details...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        <input alt='login username' className='icon un' onKeyUp={handleEnterPress} onChange={handleChange} autoComplete='off' name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        <input alt='login password' className='icon pw' onKeyUp={handleEnterPress} onChange={handleChange} autoComplete='off' name='password' type='password' placeholder='Enter your password here...' required/>
        {/* <img src={require('../../images/show.png')} style={{'height': '24px', 'marginLeft': '-30px'}} alt='display password'/> */}
      </label>
      <br/><br/><br/>
      <button className='login-button button' type='submit' onClick={(data.username && data.password) ? (login) : badLogin}>Login</button>
      <button className='login-button button' onClick={LoginHook()}>BUTTON</button>
      <br/><br/><br/>
      <h3>Don't have an account with us? <a href='/signup#signup' onClick={signup}>Sign up here!</a></h3>
    </div>
  );
}

export default LoginPage;
