import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { SessionContext } from '../../contexts/SessionContext';
// import { URLContext } from '../../contexts/URLContext';

import '../../App.scss';
import '../../index.scss';

function BreadCrumbs() {
  // const { setUrl } = useContext(URLContext);
  const { loggedIn, setLoggedIn, setUsername } = useContext(SessionContext);

  const logout = () => {
    console.log('Logout!');
    window.localStorage.setItem('loggedIn', false);
    window.localStorage.setItem('username', '');
    setLoggedIn(false);
    setUsername('');
  }

  // (window.location.href.includes("/#add-data")) &&
  const scroll = () => {
    console.log(document.getElementById('add-data'));
    const addDataTopOffset = document.getElementById('add-data').offsetTop;
    console.log(addDataTopOffset);
    document.getElementById('add-data').scrollIntoView({
      behavior: 'smooth'
    })
    // document.getElementById('add-data').scrollTo({
    //   top: addDataTopOffset,
    //   behavior: 'smooth'
    // });
    // window.scroll({
    //   top: 500,
    //   behavior: 'smooth'
    // })
  }

  return (
    // <div>
    //   BreadCrumbs
    // </div>
    <div className="center menu sticky-breadcrumbs-menu">
      <ul>
        <li><Link to="/">Homepage</Link></li>
        <li><Link to="/about">About</Link></li>

        {!loggedIn ?
        <li><Link to="/login">Login</Link></li>
        : <li><Link onClick={logout}>Logout</Link></li>}

        {!loggedIn ?
        <li><Link to="/signup">Signup</Link></li>
        : null}

        {loggedIn &&
        <li><Link to="/#add-data" onClick={scroll}>Add Data</Link></li>}
        {/* setUrl(window.location.href) */}

        {/* <!-- This is an SEO Change --> */}
        <li><Link to="https://www.youtube.com/watch?v=EsOTfVIcdEI" rel="nofollow noopener noreferrer">Learn More</Link></li>
        {/* <img src={yt_logo} onClick={() => {window.location.href = 'https://www.youtube.com/watch?v=EsOTfVIcdEI'}} rel="nofollow noopener noreferrer" alt="Personal finance 101 lesson" style={{width: "40px", height: "40px"}}/> */}

        <li><Link to="">Contact</Link></li>
      </ul>
    </div>
  );
}

export default BreadCrumbs;
