import axios from 'axios';
import React, { useState, useRef, useEffect } from 'react';


function SignupPage(props) {
  // console.log(props.loggedIn);

  const [data, setData] = useState({
    username: "",
    password: ""
  });

  const emptySignup = (e) => {
    alert(`WARNING!\nMissing signup information:\n\n${!data.username ? 'Username' : ''}\n${!data.password ? 'Password' : ''}`)
  }

  const handleChange = (e) => {
    const value = e.target.value;
    setData({
      ...data,
      [e.target.name]: value
    });
  };

  const login = () => {
    console.log('Login!');
  }

  const signup = () => {
    // data.username = document.getElementById('username').value;
    console.log('Signup!');
    console.log(data.username, data.password);

    window.location.href = '/';

    // props.loggedIn = true;

    // ...
    // ---
  }

  // Accessibility scroll
  const signupRef = useRef(null);
  // setTimeout(
  //   () => {
  //     if (signupRef.current) {
  //       signupRef.current.scrollIntoView({
  //         behavior: 'smooth'
  //       })
  //     }
  //   },
  //   250
  // );


  return(
    // <div>
    //   Signup!
    // </div>
    !props.loggedIn ?
    <div className='App'>
      {/* In-page navigation */}
      <div id='signup' ref={signupRef}/>
      <h1>SIGNUP</h1>
      <h2>Please sign up for an account below to begin budgeting...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        <input alt='login username' className='icon un' onChange={handleChange} autoComplete='off' name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        <input alt='login password' className='icon pw' onChange={handleChange} autoComplete='off' name='password' type='password' placeholder='Enter your password here...' required/>
      </label>
      <br/><br/><br/>
      <button className='signup-button button' type='submit' onClick={(data.username && data.password) ? signup : emptySignup}>Create account</button>
      <br/><br/><br/>
      <h3>Already have an account with us? <a href='/login#login' onClickCapture={login}>Login here!</a></h3>
    </div>
    : null
  )
}

export default SignupPage;
