import axios from 'axios';
import React, { useState, useContext, useEffect } from 'react';
import { Chart as ChartJS, ArcElement } from 'chart.js';
import 'chart.js/auto'
import { SessionContext } from '../contexts/SessionContext';
import { BudgetDisplayContext } from '../contexts/BudgetDisplayContext';

ChartJS.register(ArcElement);

let myDoughnutChart;

function DoughnutChart() {
  const { loggedIn, username } = useContext(SessionContext);
  const { hasBudget, setHasBudget } = useContext(BudgetDisplayContext);
  // const [ hasBudget, setHasBudget ] = useState(false);
  console.log(setHasBudget);

  function createChart(data_source) {
    if (hasBudget) {
      var ctx = document.getElementById("myChart").getContext("2d");
        if (myDoughnutChart) {
          myDoughnutChart.destroy();
        }

        myDoughnutChart = new ChartJS(ctx, {
        type: 'doughnut',
        data: data_source,
      });
    }
  };

  function getBudget() {
  	console.log('Display budget!');

  	const db_name = (loggedIn) ? 'personal-budget' : 'guest-budget';

    axios.post('http://localhost:3010/display',
      {
        db_name: db_name,
        username: username
      }
    )
    .then((data) => {
      if (data.data !== '') {
        createChart(data.data[0]);
        setHasBudget(true);
      } else {
        setHasBudget(false);
      }
    });
  };

  getBudget();
  // useEffect(() => {
  //   getBudget();
  // }, []);

  // if (loggedIn) {
    // getBudget();
    if (hasBudget) {
      return (
        <div className="chart-container">
            <canvas id="myChart"/>
        </div>
      )
    } else {
      return (
        <p>
          <br/><br/>
          Don't have any budget information displayed here?
          <br/><br/>
          <strong>Take a minute to enter in some data!</strong> 
        </p>
      );
    }
  // }
}

export default DoughnutChart;
