import axios from 'axios';
import React, { useState, useEffect } from 'react';


function SignupPage(props) {
  console.log(props.loggedIn);

  const [data, setData] = useState({
    username: "",
    password: ""
  });

  const emptySignup = (e) => {
    alert(`WARNING!\nMissing signup information:\n\n${!data.username ? 'Username' : ''}\n${!data.password ? 'Password' : ''}`)
  }

  const handleChange = (e) => {
    const value = e.target.value;
    // console.log(e.target.name, value);
    setData({
      ...data,
      [e.target.name]: value
    });
  };

  const login = () => {
    console.log('Login!');
  }

  const signup = () => {
    console.log('Signup!');
  }

  return(
    <div className='App'>
      {/* Signup! */}
      <h1>SIGNUP</h1>
      <h2>Please sign up for an account below to begin budgeting...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        <input alt='login username' className='icon un' onChange={handleChange} name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        <input alt='login password' className='icon pw' onChange={handleChange} name='password' type='password' placeholder='Enter your password here...' required/>
      </label>
      <br/><br/><br/>
      <button className='signup-button button' type='submit' onClick={(data.username && data.password) ? signup : emptySignup}>Create account</button>
      <br/><br/><br/>
      <h3>Do you have an account with us? <a href='/login' onClickCapture={login}>Login!</a></h3>
      {/* <button className='login-button' type='submit' onClick={(data.username && data.password) ? login : emptySignup}>Signup</button> */}
    </div>
  )
}

export default SignupPage;
