function LoginPage(props) {
  console.log(props.loggedIn);

  const [data, setData] = useState({
    username: "",
    password: ""
  });

  const emptyLogin = (e) => {
    alert(`WARNING!\nMissing login information:\n\n${!data.username ? 'Username' : ''}\n${!data.password ? 'Password' : ''}`)
  }

  const handleChange = (e) => {
    const value = e.target.value;
    // console.log(e.target.name, value);
    setData({
      ...data,
      [e.target.name]: value
    });
  };

  // const submit = (e) => {
  //   e.preventDefault();

  //   const value = e.target.value;
  //   console.log(value);
  // };

  const signup = () => {
    console.log('Signup!');
  }

  const login = () => {
    console.log(data.username, data.password);

    // axios
    //   .post('http://localhost:3010/login', (req, res) => {
    //     // ...
    //     // ---

    //     console.log("req.data: ", req.data);
    //     console.log("res.data: ", res.data);
    //   })
    //   .then((data) => {
    //     // ...
    //     // ---

    //     console.log(data);
    //   })
    //   .catch((err) => {
    //     // ...
    //     // ---

    //     console.log(err);
    //   })
  };

  const loginRef = useRef(null);
  setTimeout(
    () => {
      if (loginRef.current) {
        loginRef.current.scrollIntoView({
          behavior: 'smooth'
        })
      }
    },
    250
  );

  return (
    // <div>
    //   LoginPage
    // </div>
    <div className='App'>
      {/* In-page navigation */}
      <div id='login' ref={loginRef}/>
      <h1>LOGIN</h1>
      <h2>Please login below to see your budget details...</h2>
      <br/>
      <label>
        <h2>Username:</h2>
        <input alt='login username' className='icon un' onChange={handleChange} name='username' type='text' placeholder='Enter your username here...' autoFocus required/>
      </label>
      <br/><br/>
      <label>
        <h2>Password:</h2>
        <input alt='login password' className='icon pw' onChange={handleChange} name='password' type='password' placeholder='Enter your password here...' required/>
      </label>
      <br/><br/><br/>
      <button className='login-button button' type='submit' onClick={(data.username && data.password) ? login : emptyLogin}>Login</button>
      <br/><br/><br/>
      <h3>Don't have an account with us? <a href='/signup#signup' onClickCapture={signup}>Sign up here!</a></h3>
    </div>
  );
}

export default LoginPage;
