import { useState, useEffect } from 'react';


function LoginHook(data) {
  const [loggedIn, setLoggedIn] = useState(data.loggedIn);
  const [username, setUsername] = useState('');
  // console.log(data);

  // useEffect(() => {
  //   setUsername(data);
  // }, []);

  // useEffect(() => {
  //   setLoggedIn(JSON.parse(window.sessionStorage.getItem("loggedIn")));
  // }, [])
  // useEffect(() => {
  //   window.sessionStorage.setItem("loggedIn", loggedIn);
  // }, [loggedIn])

  useEffect(() => {
    data.setLoggedIn(JSON.parse(window.sessionStorage.getItem("loggedIn")));
  }, [])
  useEffect(() => {
    window.sessionStorage.setItem("loggedIn", data.loggedIn);
  }, [])
}

export default LoginHook;
